#include <bits/stdc++.h>
#define ll long long
using namespace std;
vector<string> splitString(string str, char splitter, int ws){
    vector<std::string> result;
    string current = ""; 
    for(int i = 0; i < str.size(); i++){
        if(str[i] == splitter){
            if(current != "" ){
                if(current.size() >= ws)
                result.push_back(current);
                current = "";
            } 
            continue;
        }
        current += str[i];
    }
    if(current.size() != 0)
        result.push_back(current);
    return result;
}
void solve(string x, int ws, ll & ans){
    ll temp = 1;
    for(int i=0;i<ws;i++) temp *= (x[i]-'0');
    ans = max(ans, temp);
    for(int i=ws;i<x.size();i++) {
        temp /= (x[i-ws]-'0');
        temp *= (x[i] - '0');
        ans = max(ans,temp);
    }
}
int main() {
    string x = "";
    int window_size = 13;
    vector<string> arr = splitString(x,'0',window_size);
    //for(auto i:arr) cout<<i<<" ";
    ll ans = 0;
    for(auto i : arr) solve(i, window_size, ans);
    cout<<ans;
    return 0;
}
